import React, {useState} from 'react';
import Form from './components/Form';
import TodoItemList from './components/TodoItemList';
import TodoListTemplate from './components/TodoListTemplate';

let id = 3;

const reducer = ({input, todos}, action)=>{
  switch(action.type){
    case 'changeInput': 
      return {input: action.e.target.value};

    case 'addTodo':
      return {
        input: '',
        todos: todos.concat({
          id: id++,
          text: input,
          checked: false
        })
      };

    case 'keyPress':
      if(action.e.type == 'Enter')
        dispatch({type: 'addTodo'});
      break;

    case 'toggleTodo':
      const index = todos.findIndex(todo => todo.id === action.id);
      const selected = todos[index]; // 선택한 객체

      const nextTodos = [...todos]; // 배열을 복사

      // 기존의 값들을 복사하고, checked 값을 덮어쓰기
      nextTodos[index] = { 
        ...selected, 
        checked: !selected.checked
      };

      return {todos: nextTodos};

    case 'removeTodo':
      return {todos: todos.filter(todo => todo.id !== id)};
  }
}

const App = ()=>{
  const [state, dispatch] = useReducer(reducer, {
    input: '',
    todos: [
      { id: 0, text: '리액트 소개0', checked: false },
      { id: 1, text: '리액트 소개1', checked: true },
      { id: 2, text: '리액트 소개2', checked: false }
    ]
  });
