  return (
    <TodoListTemplate form={(
      <Form 
        value={state.input}
        onKeyPress={(e)=>{dispatch({
          type: 'keyPress',
          e: e
        })}}
        onChange={(e)=>{dispatch({
          type: 'changeInput',
