let id = 3;

const reducer = (state, action)=>{
  switch(action.type){
    case 'changeInput': 
      return {input: e.target.value};
    case 'addTodo':
      return {
        input: '',
        todos: state.todos.concat({
          id: id++,
          text: input,
          checked: false
        })
      };
  }
}

